definition.script:
pipeline {
    agent any
    stages {
        stage('test') {
            steps {
                script {
                    userInput = input(
                        id:'userInput',
                        message: 'Choose jar version ',
                        parameters: [[
                            $class: 'MavenMetadataParameterDefinition',
                            name: 'version',
                            description:'',
                            repoBaseUrl: 'http://repo.jenkins-ci.org/public',
                            groupId: '${project.groupId}',
                            artifactId: '${project.artifactId}',
                            packaging: '${project.packaging}',
                            defaultValue: '',
                            classifier:'',
                            versionFilter: '',
                            sortOrder: 'DESC',
                            maxVersions: '',
                            currentArtifactInfoUrl: '',
                            currentArtifactInfoLabel: '',
                            currentArtifactInfoPattern: '',
                            credentialsId: ''
                        ]],
                    )
                    print("Chosen version : " + userInput)
                }
            }
        }
    }
}
